const env = $env || {};

function toDateKey(dateObj) {
  const year = dateObj.getUTCFullYear();
  const month = String(dateObj.getUTCMonth() + 1).padStart(2, '0');
  const day = String(dateObj.getUTCDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
}

const targetDate = String(env.N8N_METRICS_TARGET_DATE || '').trim();
if (targetDate && !/^\d{4}-\d{2}-\d{2}$/.test(targetDate)) {
  return [{ json: { ok: false, status_code: 400, message: 'N8N_METRICS_TARGET_DATE must be YYYY-MM-DD' } }];
}

const now = new Date();
const utcMidnightToday = new Date(Date.UTC(
  now.getUTCFullYear(),
  now.getUTCMonth(),
  now.getUTCDate(),
  0,
  0,
  0
));
const utcMidnightYesterday = new Date(utcMidnightToday.getTime() - 24 * 60 * 60 * 1000);
const expectedYesterday = toDateKey(utcMidnightYesterday);

const dt = targetDate || expectedYesterday;

return [
  {
    json: {
      ok: true,
      status_code: 200,
      expected_yesterday_utc: expectedYesterday,
      dt,
      target_date: targetDate || null
    }
  }
];
