const body = ($json && typeof $json === 'object' && $json.body && typeof $json.body === 'object') ? $json.body : ($json ?? {});

const realm = String(body.realm || process.env.N8N_REALM || 'default').trim() || 'default';
const principalId = String(body.principal_id || body.principalId || '').trim();

if (!principalId) {
  throw new Error('principal_id is required');
}

const requestedBy = (body.requested_by && typeof body.requested_by === 'object') ? body.requested_by : {};

return [{ json: { realm, principal_id: principalId, requested_by: requestedBy } }];
