function parseIntSafe(raw, fallback) {
  const n = Number.parseInt(String(raw ?? ''), 10);
  return Number.isFinite(n) && n >= 0 ? n : fallback;
}

const body = ($json && typeof $json === 'object' && $json.body && typeof $json.body === 'object') ? $json.body : ($json ?? {});
const realm = String(body.realm || process.env.N8N_REALM || 'default').trim() || 'default';
const maxRows = parseIntSafe(body.max_rows ?? process.env.ITSM_SOR_RETENTION_MAX_ROWS, 500);

return [{ json: { realm, max_rows: maxRows } }];
