const body = ($json && typeof $json === 'object' && $json.body && typeof $json.body === 'object') ? $json.body : ($json ?? {});
const headers = ($json && typeof $json === 'object' && $json.headers && typeof $json.headers === 'object') ? $json.headers : {};

const fail = (statusCode, message) => ([{ json: { ok: false, status_code: statusCode, error: message } }]);

const isUuid = (value) => {
  const s = String(value ?? '').trim();
  return /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i.test(s);
};

const token = String($env.ITSM_SOR_WEBHOOK_TOKEN || '').trim();
if (token) {
  const auth = String(headers.authorization || headers.Authorization || '').trim();
  const xToken = String(headers['x-itsm-sor-token'] || headers['X-ITSM-SOR-TOKEN'] || '').trim();
  const bearer = auth.toLowerCase().startsWith('bearer ') ? auth.slice('bearer '.length).trim() : '';
  if (bearer !== token && xToken !== token) {
    return fail(401, 'unauthorized');
  }
}

const realm = String(body.realm || $env.N8N_REALM || $env.N8N_OBSERVER_REALM || 'default').trim() || 'default';

const approvalId = body.approval_id ?? body.approvalId ?? body.id ?? null;
if (!approvalId) return fail(400, 'approval_id is required');
if (!isUuid(approvalId)) return fail(400, 'approval_id must be uuid');

const contextId = body.context_id ?? body.contextId ?? body.resource_id ?? body.resourceId ?? null;
if (!contextId) return fail(400, 'context_id is required');
if (!isUuid(contextId)) return fail(400, 'context_id must be uuid');

const correlationId = body.correlation_id ?? body.correlationId ?? body.trace_id ?? body.traceId ?? null;

const actor = (body.actor && typeof body.actor === 'object') ? body.actor : {};
const jobPlan = (body.job_plan && typeof body.job_plan === 'object') ? body.job_plan : ((body.after && typeof body.after === 'object') ? body.after : {});
const replyTarget = (body.reply_target && typeof body.reply_target === 'object') ? body.reply_target : {};

const occurredAt = body.occurred_at || body.occurredAt || null;

const rawDecision = String(body.decision ?? body.status ?? '').trim().toLowerCase();
if (!rawDecision) return fail(400, 'decision is required');
let decision = rawDecision;
if (['approve', 'approved', 'yes', 'y', 'ok'].includes(rawDecision)) decision = 'approved';
if (['deny', 'denied', 'rejected', 'no', 'n', 'ng'].includes(rawDecision)) decision = 'rejected';
if (!decision) decision = 'rejected';

const status = decision === 'approved' ? 'approved' : 'rejected';

const approvedByPrincipalId = String(body.approved_by_principal_id || body.approvedByPrincipalId || actor.sub || actor.principal_id || actor.email || '').trim() || null;

const eventKey = String(body.event_key || body.eventKey || `aiops:approval:${approvalId || 'none'}:${rawDecision || decision}`).trim();
if (!eventKey) return fail(400, 'event_key is required');

const source = String(body.source || 'aiops_agent').trim() || 'aiops_agent';

return [{
  json: {
    ok: true,
    record_type: 'sor_aiops_approval_decision',
    realm,
    occurred_at: occurredAt,
    approval_id: approvalId,
    context_id: contextId,
    decision,
    status,
    approved_by_principal_id: approvedByPrincipalId,
    actor,
    job_plan: jobPlan,
    reply_target: replyTarget,
    correlation_id: correlationId,
    source,
    integrity: {
      event_key: eventKey
    }
  }
}];
